    //3.String -- Find the unique
    //Write a return  method that can find the unique characters from the String
    //Ex:  unique("AAABBBCCCDEF")  ==>  "DEF";
    public static String uniqThruSET(String word) {
        if (word.isEmpty() || word.isBlank()) {
            throw new IllegalArgumentException("String isEmpty or Blank !!!!");
        }

        String str = "";
        Set<Character> set = new HashSet<>();

        for (int i = 0; i < word.length(); i++) {
            Character ch = word.charAt(i);
            set.add(ch);
        }
        for (Character each : set) {
            str += each;
        }
        return str;
    }

    public static String uniq1ThruContains(String word) {
        if (word.isEmpty() || word.isBlank()) {
            throw new IllegalArgumentException("String isEmpty or Blank !!!!");
        }
        String result = "";
        for (int i = 0; i < word.length(); i++) {
            if (result.contains("" + word.charAt(i))) {
                continue;
            } else {
                result += word.charAt(i);
            }
        }
        return result;
    }

    public static String uniqThruIndexOf(String word) {
        if (word.isEmpty() || word.isBlank()) {
            throw new IllegalArgumentException("String isEmpty or Blank !!!!");
        }

        String result = "";
        for (int i = 0; i < word.length(); i++) {
            if (result.indexOf(word.charAt(i)) == -1) {
                result += word.charAt(i);
            }
        }
        return result;
    }


    //4) String - Reverse
    //Write a return method that can reverse String
    //Ex: Reverse("ABCD"); ==> DCBA
    public static String reversThruStringBuilder(String word) {
        if (word.isEmpty() || word.isBlank()) {
            throw new IllegalArgumentException("String isEmpty or Blank !!!!");
        }
        StringBuilder str1 = new StringBuilder();
        for (int i = word.length() - 1; i >= 0; i--) {
            str1.append(word.charAt(i));
        }
        String rev = str1.toString();
        return rev;
    }

    public static String reversThruString(String word) {
        if (word.isEmpty() || word.isBlank()) {
            throw new IllegalArgumentException("String isEmpty or Blank !!!!");
        }
        String result = "";
        for (int i = word.length() - 1; i >= 0; i--) {
            result += "" + word.charAt(i);
        }
        return result;
    }


